<?php

include '../assets/includes/config.php';

// SWITCH BETWEEN CATEGORY'S & SEARCH UPLOADS QUERY
 if (isset($_POST['overview_submit_search'])) {
    $searchterm = mysqli_real_escape_string($dbc,htmlspecialchars($_POST['overview_search']));
    $searchterm = '%' . $searchterm . '%';
} else {
    $searchterm = '%';
}

// CHECK IF CATEGORY FORM IS SUBMITTED
// https://stackoverflow.com/questions/7711466/checking-if-form-has-been-submitted-php
if ($_SERVER['REQUEST_METHOD'] == 'POST' && !empty($_POST['overview_category'])) {
    $searchterm = mysqli_real_escape_string($dbc,htmlspecialchars($_POST['overview_category']));
    $searchterm = '%' . $searchterm . '%';
}

// SORT UPLOADS QUERY
$sort_by = 'upload_id';
$sort_order = 'DESC';
if(isset($_POST['overview_sort'])) {
    $sortDropdown = mysqli_real_escape_string($dbc,htmlspecialchars($_POST['overview_sort']));
    switch ($sortDropdown) {
      case 'date_desc':
        $sort_by = 'upload_date';
        $sort_order = 'ASC';
      break;

      case 'date_asc':
        $sort_by = 'upload_date';
        $sort_order = 'DESC';
      break;

      case 'date_random':
        $sort_by = 'rand()';
        $sort_order = '';
      break;
    }
  }

// GET UPLOADS (SHOTS) FROM DATABASE
