<script>
    $(document).ready(function() {  
        document.querySelector('body').classList.add('upload-details');

        // COPY UPLOAD LINK ON CLICK (SHARE)
        // https://www.w3schools.com/howto/tryit.asp?filename=tryhow_js_copy_clipboard
        document.querySelector('main section .overview-shots-overlay-details .shot-details-content .shot-details-content-right .button-share-upload').onclick = function() {
            var uploadLink = document.getElementById("hidden-link");
            uploadLink.select();
            uploadLink.setSelectionRange(0, 99999);
            document.execCommand("copy");

            // ALERT AFTER LINK HAS BEEN COPIED
            document.querySelector('main section .alert-share').classList.add('visible');
            setTimeout(function() {
                document.querySelector('main section .alert-share').classList.remove('visible');
            }, 4500);
        };
    });
</script>